import React from 'react'
import ReactDOM from 'react-dom'

const Header = (props) => {
    return (
        <div>
            <h1>{props.course}</h1>
        </div>
    )
}

const Osa = (props) => {
    return (
        <div>
            <p>{props.Part} {props.tehtavia}</p>
        </div>
    )
}

const Sisalto = (props) => {
    return (
        <div>
            <Part Part={props.Part1} tehtavia={props.tehtavia1} />
            <Part Part={props.Part2} tehtavia={props.tehtavia2} />
            <Part Part={props.Part3} tehtavia={props.tehtavia3} />
        </div>
    )
}

const Yhteensa = (props) => {
    return (
        <div>
            <p>yhteensä {props.yhteensa} tehtävää</p>
        </div>  
    )  
}

const App = () => {
  const course = 'Half Stack -sovelluskehitys'
  const osa1 = 'Reactin perusteet'
  const tehtavia1 = 10
  const osa2 = 'Tiedonvälitys propseilla'
  const tehtavia2 = 7
  const osa3 = 'Komponenttien tila'
  const tehtavia3 = 14

  return (
    <div>
        <Header course={course} />
        <Sisalto osa1={osa1} osa2={osa2} osa3={osa3}
         tehtavia1={tehtavia1} tehtavia2={tehtavia2} tehtavia3={tehtavia3} />
        <Yhteensa yhteensa={tehtavia1 + tehtavia2 + tehtavia3} />
    </div>    
  )
}

ReactDOM.render(
  <App />,
  document.getElementById('root'));
