import React from 'react'
import ReactDOM from 'react-dom'

const Header = (props) => {
  return(
    <div>
    {props.course.name}
    </div>
  ) 
};

const Part1 = (props) => {
  return (
    <div>
      <h3>
        {props.part1} {":"} {props.exercises1}
      </h3>
    </div>
  );
};

const Part2 = (props) => {
  return (
    <div>
      <h3>
        {props.part2} {":"} {props.exercises2}
      </h3>
    </div>
  );
};

const Part3 = (props) => {
  return (
    <div>
      <h3>
        {props.part3} {":"} {props.exercises3}
      </h3>
    </div>
  );
};

const Content = (props) => {
  return (
    <div>
      <Part1
        part1={props.course.parts[0].name}
        exercises1={props.course.parts[0].exercises}
      />
      <Part2
        part2={props.course.parts[1].name}
        exercises2={props.course.parts[1].exercises}
      />
      <Part3
        part3={props.course.parts[2].name}
        exercises3={props.course.parts[2].exercises}
      />
    </div>
  );
};

const Total = (props) => {
  return (
    props.course.parts[0].exercises +
    props.course.parts[1].exercises +
    props.course.parts[2].exercises
  );
};

const App = () => {
  const course = {
    name: "Half Stack application development",
    parts: [
      {
        name: "Fundamentals of React",
        exercises: 10,
      },

      {
        name: "Using props to pass data",
        exercises: 7,
      },

      {
        name: "State of a component",
        exercises: 14,
      },
    ],
  };

  return (
    <div>
      <h1>
        <Header course={course} />
      </h1>
      <Content course={course} />
      <Total course={course} />
    </div>
  );
};

ReactDOM.render(

    <App />
  ,
  document.getElementById("root")
);
